function Register() {

  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: '',
    agreeTerms: false,
  });

  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState('');
  const [showErrorAlert, setShowErrorAlert] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const validateForm = () => {
    const validationErrors = {};

    if (!formData.username.trim()) {
      validationErrors.username = 'Username is required.';
    }

    if (!formData.email.trim()) {
      validationErrors.email = 'Email is required.';
    } else if (!/^\S+@\S+\.\S+$/.test(formData.email)) {
      validationErrors.email = 'Invalid email address.';
    }

    if (!formData.password.trim()) {
      validationErrors.password = 'Password is required.';
    }

    if (formData.password !== formData.confirmPassword) {
      validationErrors.confirmPassword = 'Passwords do not match.';
    }

    if (!formData.agreeTerms) {
      validationErrors.agreeTerms = 'You must agree to the terms and conditions.';
    }

    setErrors(validationErrors);

    return Object.keys(validationErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (validateForm()) {
      try {
        // Simulate server request
        const response = await axios.post('/api/auth/signup', formData);

        setSuccessMessage(response.data.message);

        setTimeout(() => {
          navigate('/posts');
        }, 2000);
      } catch (error) {
        setShowErrorAlert(true);
        setErrors({ general: 'Failed to sign up. Please try again.' });
      }
    }
  };

  return (

    <div className="container-fluid w-25 border border-dark d-flex justify-content-center mt-4">
    <form onSubmit={handleSubmit} className="signup-form mb-5">
      <h1>SignupForm</h1>
      <div className="form-group p-3">
        <label>
          Username:
          <input
            type="text"
            className="form-control w-100"
            name="username"
            value={formData.username}
            onChange={handleChange}
            required
          />
          {errors.username && <p className="error-message">{errors.username}</p>}
        </label>
      </div>

      <div className="form-group p-3">
        <label>
          Email:
          <input
            type="email"
            className="form-control"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
          {errors.email && <p className="error-message">{errors.email}</p>}
        </label>
      </div>

      <div className="form-group p-3">
        <label>
          Password:
          <input
            type="password"
            className="form-control"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
          {errors.password && <p className="error-message">{errors.password}</p>}
        </label>
      </div>

      <div className="form-group p-3">
        <label>
          Confirm Password:
          <input
            type="password"
            className="form-control"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleChange}
            required
          />
          {errors.confirmPassword && (
            <p className="error-message">{errors.confirmPassword}</p>
          )}
        </label>
      </div>

      <div className="form-group p-3">
        <label>
          <input
            type="checkbox"
            name="agreeTerms"
            checked={formData.agreeTerms}
            onChange={handleChange}
          />
          Agree to terms and conditions
          {errors.agreeTerms && <p className="error-message">{errors.agreeTerms}</p>}
        </label>
      </div>

      <button type="submit">Sign Up</button>

      {errors.general && <p className="error-message">{errors.general}</p>}
      {showErrorAlert && (
        <div className="error-alert">Failed to sign up. Please try again.</div>
      )}
      {successMessage && <p className="success-message">{successMessage}</p>}
    </form>
  </div>


  )
}

export default Register
