import React,{useState,useNavigate} from 'react'
import "./App.css"
import axios from "axios"

function SignupForm() {
 const navigate = useNavigate();
 const [formData,setFormData] = useState({
    username:"",
    email:"",
    password:"",
    confirmPassword:"",
    name:"",
    profilePicture:"",
    agreeTerms:false,
 })

 const [error,setError]= useState(null);
 const[successMessage,setSuccessMessage] = useState(null);

 const handleChange = (e)=>{
    setFormData({...formData,[e.target.name]:e.target.value});
 };


 const handleSubmit = async(e)=>{
    e.preventDefault();




    try{
        if(!formData.agreeTerms){
            throw new Error('please agree to the terms and conditions.');
        }

        const response = await axios.post('/api/signup',formData);

        setSuccessMessage(response.data.message);
            


        setTimeout(()=>{
            navigate.push('/posts');
        },2000);

        }catch(error){
            setError(error.message);
        }
 };






  return (
 <form onSubmit={handleSubmit} >
    <label>
        Username:
        <input type='text' name="username" value={FormData.username} onChange={handleChange} required />
    </label>
    <label>
        Email:
        <input type="email" name="email" value={formData.email} onChange={handleChange} required />
      </label>

      <label>
        Password:
        <input type="password" name="password" value={formData.password} onChange={handleChange} required />
      </label>

      <label htmlFor="password">
        Confirm Password:
        <input type="password" id="password" name="confirmPassword" value={formData.confirmPassword} onChange={handleChange} required />
      </label>

      <label>
        Name:
        <input type="text" name="name" value={formData.name} onChange={handleChange} />
      </label>

      <label>
        Profile Picture URL:
        <input type="text" name="profilePicture" value={formData.profilePicture} onChange={handleChange} />
      </label>

      <label>
        <input type="checkbox" name="agreeTerms" checked={formData.agreeTerms} onChange={handleChange} />
        Agree to terms and conditions
      </label>

      <button type="submit">Sign Up</button>

      {error && <p style={{ color: 'red' }}>{error}</p>}
      {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}

 </form>
  )
}

export default SignupForm
